<?php/*	Đây là nơi có thể xem là vào chào đầu tiên khi người dùng truy cập vào hệ thống PHPPV*/session_start();
include_once('config/config.php');include_once('function/function.php');include_once('class/mysql.php');include_once('class/xtemplate/xtemplate.class.php');if (!RUNTIME_DEV_MODE) {	error_reporting(E_ERROR | E_WARNING | E_PARSE);}$sql = new MySQL();save_user_ip();if ($_SESSION['login'] <> true) {	$_SESSION['login'] = false;	outpage('Bạn phải đăng nhập mới được sử dụng chức năng này !');}if (RUNTIME_MOBILE_DETECT) {	$tpl = new XTemplate('template/mobile/setting.tpl');} else {	$tpl = new XTemplate('template/setting.tpl');}if ($_POST <> array()) {	foreach ($_POST as $id => $value) {		$sql->update_setting($id, $value);	}}$search = $sql->setquery('SELECT * FROM `setting`');if (mysql_num_rows($search) == 0) {	$tpl->parse('main.no_setting');} else {	while ($result = mysql_fetch_assoc($search)) {		$tpl->set_autoreset();				$tpl->assign('id', $result['id']);		$tpl->assign('type', $result['type']);		$tpl->assign('name', $result['name']);		$tpl->assign('description', $result['description']);		$tpl->assign('value', htmlspecialchars($result['value']));		$tpl->assign('updated', $result['updated']);				$tpl->parse('main.setting');	}}$sql->close();$tpl->parse('main');$tpl->out('main');
?>