<?php/*	Đây là file cung cấp chức năng cập nhật bảng điểm mới vào website bằng cách tải lên bảng điểm dưới dạng file dữ liệu.*/if ($upload_check !== true) header('Location: index.php');
include_once('config/config.php');include_once('function/function.php');//include_once('class/mysql.php');//if the error occurs, it will be place in this$upload_error = false;while (true) {			// stop script if error detected (using break; for stoping)     // get file name (not including path)	$filename = basename($_FILES['update_db']['name']);	$filename = clear_invalid_fname(uni2non_uni($filename));	if (strpos($filename, '.') == false) {		$upload_error = 'Không thể xác định phần mở rộng của file (đuôi file).';		break;	}    $file_ext = end(explode('.', $filename));	$file_type = $_FILES['update_db']['type'];	$is_disallow_file = true;	foreach (array('xls', 'csv') as $ext => $type) {		if (($file_ext == $ext) or ($file_type == $type)) {			$is_disallow_file = false;		}	}	if ($is_disallow_file == true) {		$upload_error = 'Loại file <b>('.$file_ext.')</b> bạn upload không được cho phép.';		break;	}    // destination filename, rename if set to	$dest_filename = $filename;	if (UPLOAD_FNAME_RANDOM) {		$dest_filename = md5(uniqid(rand(), true)).'.'.$file_ext;	}    // append predefined string for safety	$dest_filename = $dest_filename.UPLOAD_FNAME_APPEND_STRING;	// filename of temp uploaded file	$tmp_filename = $_FILES['update_db']['tmp_name'];	if (!move_uploaded_file($tmp_filename , UPLOAD_FOLDER.$dest_filename)) {		$upload_error = 'Không thể di chuyển tmp file trên server';		break;	}	break;}?>